import React, { useState } from 'react';

// import './EmployeeRegistrationForm.css';

function EmployeeRegistrationForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    maritalStatus: '',
    contactNumber: '',
    email: '',
    permanentAddress: '',
    currentAddress: '',
    hasDisability: '',
    previousExperience: '',
    nationality: '',
    workPermitCountry: '',
    workPermitExpiry: '',
    emergencyContactName: '',
    emergencyRelationship: '',
    emergencyContactNumber: '',
    bankName: '',
    bankBranch: '',
    accountNumber: '',
    ifscCode: '',
    highestQualification: '',
    previousEmployer: '',
    previousDesignation: '',
    yearsOfExperience: '',
    currentDesignation: '',
    department: '',
    employeeId: '',
    dateOfJoining: '',
    declarationAccepted: false,
    agreedTerms: false,
    declarationDate: '',
    isCurrentAddressSameAsPermanent: false
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setFormData((prev) => {
        const newFormData = { ...prev, [name]: checked };
        if (name === 'isCurrentAddressSameAsPermanent' && checked) {
          newFormData.currentAddress = newFormData.permanentAddress; // Automatically copy permanent address to current address
        } else if (name === 'isCurrentAddressSameAsPermanent' && !checked) {
          newFormData.currentAddress = ''; // Clear current address if unchecked
        }

        if (name === 'agreedTerms' && checked) {
         alert('By checking the box you are agreeing to the Terms & Conditions and Privacy policy.');
        }
        return newFormData;
      });
    } else if (type === 'file') {
      setFormData((prev) => ({ ...prev, [name]: files[0] }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Validation logic
    let validationErrors = {};
    if (!formData.firstName.trim()) validationErrors.firstName = "First name is required.";
    if (!formData.lastName.trim()) validationErrors.lastName = "Last name is required.";
    if (!formData.dateOfBirth) validationErrors.dateOfBirth = "Date of birth is required.";
    if (!formData.gender) validationErrors.gender = "Gender is required.";
    if (!formData.maritalStatus) validationErrors.maritalStatus = "Marital status is required.";
    if (!formData.contactNumber.match(/^\d{10}$/)) validationErrors.contactNumber = "Enter a valid 10-digit contact number.";
    if (!formData.email.match(/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/)) validationErrors.email = "Enter a valid email address.";
    if (!formData.permanentAddress.trim()) validationErrors.permanentAddress = "Permanent address is required.";
    if (!formData.nationality.trim()) validationErrors.nationality = "Nationality is required.";
    if (!formData.bankName.trim()) validationErrors.bankName = "Bank name is required.";
    if (!formData.bankBranch.trim()) validationErrors.bankBranch = "Branch is required.";
    if (!formData.accountNumber.match(/^\d{9,18}$/)) validationErrors.accountNumber = "Enter a valid account number.";
    if (!formData.ifscCode.match(/^[A-Z]{4}0[A-Z0-9]{6}$/)) validationErrors.ifscCode = "Enter a valid IFSC code.";
    if (!formData.currentDesignation.trim()) validationErrors.currentDesignation = "Current designation is required.";
    if (!formData.department.trim()) validationErrors.department = "Department is required.";
    if (!formData.employeeId.trim()) validationErrors.employeeId = "Employee ID is required.";
    if (!formData.dateOfJoining) validationErrors.dateOfJoining = "Date of joining is required.";
    if (!formData.agreedTerms) validationErrors.agreedTerms = "You must agree to terms and conditions.";
    if (!formData.declarationDate) validationErrors.declarationDate = "Declaration date is required.";
  
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      console.log(validationErrors);
      alert("Please correct the highlighted errors.");
      return;
    }
  
    // Submit data to the backend
    try {
      const response = await fetch("http://localhost:8070", { //server true= 200-299, false= 400-499
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
  
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
  
      const result = await response.json();
      console.log("Submitted Data:", result);
      alert("Form submitted successfully!");
      setFormData({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        maritalStatus: '',
        contactNumber: '',
        email: '',
        permanentAddress: '',
        currentAddress: '',
        hasDisability: '',
        previousExperience: '',
        nationality: '',
        workPermitCountry: '',
        workPermitExpiry: '',
        emergencyContactName: '',
        emergencyRelationship: '',
        emergencyContactNumber: '',
        bankName: '',
        bankBranch: '',
        accountNumber: '',
        ifscCode: '',
        highestQualification: '',
        previousEmployer: '',
        previousDesignation: '',
        yearsOfExperience: '',
        currentDesignation: '',
        department: '',
        employeeId: '',
        dateOfJoining: '',
        declarationAccepted: false,
        agreedTerms: false,
        declarationDate: '',
        isCurrentAddressSameAsPermanent: false,
      });
      setErrors({});
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Failed to submit the form. Please try again later.");
    }
  };



  const handleReset = () => 
    {
      const confirmReset = window.confirm("Are you sure to reset the form?");
      if (confirmReset) {
       setFormData({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        maritalStatus: '',
        contactNumber: '',
        email: '',
        permanentAddress: '',
        currentAddress: '',
        hasDisability: '',
        previousExperience: '',
        nationality: '',
        workPermitCountry: '',
        workPermitExpiry: '',
        emergencyContactName: '',
        emergencyRelationship: '',
        emergencyContactNumber: '',
        bankName: '',
        bankBranch: '',
        accountNumber: '',
        ifscCode: '',
        highestQualification: '',
        previousEmployer: '',
        previousDesignation: '',
        yearsOfExperience: '',
        currentDesignation: '',
        department: '',
        employeeId: '',
        dateOfJoining: '',
        declarationAccepted: false,
        agreedTerms: false,
        declarationDate: '',
        isCurrentAddressSameAsPermanent: false
      });
      setErrors({});
      alert("Form has been reset!");
    }
  };

    return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center py-8">
      <img
        src="https://www.ascentware.in/images/logo.png"
        alt="Company Logo"
        className="w-40 mb-6"
      />
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-3xl">
        <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">EMPLOYEE REGISTRATION</h2>
        <form onSubmit={handleSubmit}>
          <h3 className="text-lg font-semibold mb-4 text-blue-600">PERSONAL DETAILS</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block font-medium">First Name:</label>
              <input type="text" name="firstName" value={formData.firstName} onChange={handleChange}
                className={`mt-1 block w-full rounded border ${errors.firstName ? "border-red-500" : "border-gray-300"} p-2`} />
              {errors.firstName && <span className="text-red-500 text-xs">{errors.firstName}</span>}
            </div>
            <div>
              <label className="block font-medium">Middle Name (Optional):</label>
              <input type="text" name="middleName" value={formData.middleName} onChange={handleChange}
                className="mt-1 block w-full rounded border border-gray-300 p-2" />
            </div>

            <div>
              <label className="block font-medium">Last Name:</label>
              <input type="text" name="lastName" value={formData.lastName} onChange={handleChange}
                className={`mt-1 block w-full rounded border ${errors.lastName ? "border-red-500" : "border-gray-300"} p-2`} />
              {errors.lastName && <span className="text-red-500 text-xs">{errors.lastName}</span>}
            </div>

            <div>
              <label className="block font-medium">Date of Birth:</label>
              <input type="date" name="dateOfBirth" value={formData.dateOfBirth} onChange={handleChange} min="1995-01-01" max="2025-01-01"
                className={`mt-1 block w-full rounded border ${errors.dateOfBirth ? "border-red-500" : "border-gray-300"} p-2`} />
              {errors.dateOfBirth && <span className="text-red-500 text-xs">{errors.dateOfBirth}</span>}
            </div>

            <div>
              <label className="block font-medium">Gender:</label>
              <div className="flex items-center">
                <input type="radio" name="gender" value="Male" checked={formData.gender === 'Male'} onChange={handleChange} className="mr-2" /> Male
                <input type="radio" name="gender" value="Female" checked={formData.gender === 'Female'} onChange={handleChange} className="mx-2" /> Female
                <input type="radio" name="gender" value="Other" checked={formData.gender === 'Other'} onChange={handleChange} className="ml-2" /> Other
              </div>
              {errors.gender && <span className="text-red-500 text-xs">{errors.gender}</span>}
            </div>

            <div>
              <label className="block font-medium">Marital Status:</label>
              <div className="flex items-center">
                <input type="radio" name="maritalStatus" value="Single" checked={formData.maritalStatus === 'Single'} onChange={handleChange} className="mr-2" /> Single
                <input type="radio" name="maritalStatus" value="Married" checked={formData.maritalStatus === 'Married'} onChange={handleChange} className="mx-2" /> Married
                <input type="radio" name="maritalStatus" value="Others" checked={formData.maritalStatus === 'Others'} onChange={handleChange} className="ml-2" /> Others
              </div>
              {errors.maritalStatus && <span className="text-red-500 text-xs">{errors.maritalStatus}</span>}
            </div>

            <div>
              <label className="block font-medium">Contact Number:</label>
              <input type="tel" name="contactNumber" value={formData.contactNumber} onChange={handleChange}
                className={`mt-1 block w-full rounded border ${errors.contactNumber ? "border-red-500" : "border-gray-300"} p-2`} />
              {errors.contactNumber && <span className="text-red-500 text-xs">{errors.contactNumber}</span>}
            </div>

            <div>
              <label className="block font-medium">Email ID:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="abc@xyz.com"
                className={`mt-1 block w-full rounded border ${errors.email ? "border-red-500" : "border-gray-300"} p-2`} />
              {errors.email && <span className="text-red-500 text-xs">{errors.email}</span>}
            </div>


          <div>
              <label className="block font-medium">Permanent Address:</label>
            <textarea name="permanentAddress" value={formData.permanentAddress} onChange={handleChange}
              className={`mt-1 block w-full rounded border ${errors.permanentAddress ? "border-red-500" : "border-gray-300"} p-2`} />
            {errors.permanentAddress && <span className="text-red-500 text-xs">{errors.permanentAddress}</span>}
            </div>

            <div className="flex items-center space-x-2">
  <label className="mr-2">Is the current address same as permanent address?</label>
  <input type="checkbox" name="isCurrentAddressSameAsPermanent" checked={formData.isCurrentAddressSameAsPermanent} onChange={handleChange}/>
</div>

            <div>
            <label className="block font-medium">Current Address:</label>
           <textarea name="currentAddress" value={formData.currentAddress} onChange={handleChange} disabled={formData.isCurrentAddressSameAsPermanent}
             className="mt-1 block w-full rounded border border-gray-300 p-2" />
          </div>

           <div>
              <label className="block font-medium">Nationality:</label>
              <input type="text" name="nationality" value={formData.nationality} onChange={handleChange}
                className={`mt-1 block w-full rounded border ${errors.nationality ? "border-red-500" : "border-gray-300"} p-2`} />
              {errors.nationality && <span className="text-red-500 text-xs">{errors.nationality}</span>}
            </div>
          </div>

          <div className="full-width">
             <label className="block font-medium">PWD(Person with Disability) Status</label>
           
             <div>
              <label>
              <input type="radio" name="hasDisability" value="yes" checked={formData.hasDisability === "yes"}
               onChange={handleChange}/>{" "} Yes
            </label>
            <label style={{ marginLeft: "1px" }}>
             <input type="radio" name="hasDisability" value="no" checked={formData.hasDisability === "no"}
             onChange={handleChange}/>{" "}No
            </label>
           </div>
          </div>

          {/* Conditional Message */}
          {formData.hasDisability === "yes" && (
          <div className="w-full text-red-600 font-bold">
    If Yes (Request you to share the PWD documents with details and % of disability)
  </div>
         )}


         <div>
           <label className="block font-medium">Work Permit Available Country:</label>
           <input type="text" name="workPermitCountry" value={formData.workPermitCountry} onChange={handleChange}
             className="mt-1 block w-full rounded border border-gray-300 p-2" />
         </div>

         <div>
           <label className="block font-medium">Work Permit Expiry Date:</label>
           <input type="date" name="workPermitExpiry" value={formData.workPermitExpiry} onChange={handleChange}
             className="mt-1 block w-full rounded border border-gray-300 p-2" />
         </div>
        
         {/* EMERGENCY CONTACTS */}

         <h3 className="text-lg font-semibold mb-4 text-blue-600">EMERGENCY CONTACT DETAILS</h3>
         <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block font-medium">Emergency Contact Name:</label>
            <input type="text" name="emergencyContactName" value={formData.emergencyContactName} onChange={handleChange}
              className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.emergencyContactName && <span className="text-red-500 text-xs">{errors.emergencyContactName}</span>}
         </div>
         <div>
          <label className="block font-medium">Relationship:</label>
          <input type="text" name="emergencyRelationship" value={formData.emergencyRelationship} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.emergencyRelationship && <span className="text-red-500 text-xs">{errors.emergencyRelationship}</span>}
         </div>
          <div>
           <label className="block font-medium">Emergency Contact Number:</label>
            <input type="tel"  name="emergencyContactNumber"  value={formData.emergencyContactNumber} onChange={handleChange}
             className="mt-1 block w-full rounded border border-gray-300 p-2" />
            {errors.emergencyContactNumber && <span className="text-red-500 text-xs">{errors.emergencyContactNumber}</span>}
         </div>
         </div>

          {/* Bank and Salary Details */}

           <h3 className="text-lg font-semibold mb-4 text-blue-600">BANK & SALARY DETAILS</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
           <label className="block font-medium">Bank Name:</label>
           <input type="text" name="bankName" value={formData.bankName} onChange={handleChange}
             className="mt-1 block w-full rounded border border-gray-300 p-2" />
           {errors.bankName && <span className="text-red-500 text-xs">{errors.bankName}</span>}
          </div>
          <div>
            <label className="block font-medium">Branch:</label>
            <input type="text" name="bankBranch" value={formData.bankBranch} onChange={handleChange}
              className="mt-1 block w-full rounded border border-gray-300 p-2" />
           {errors.bankBranch && <span className="text-red-500 text-xs">{errors.bankBranch}</span>}
         </div>
         <div>
          <label className="block font-medium">Account Number:</label>
          <input type="text" name="accountNumber" value={formData.accountNumber} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.accountNumber && <span className="text-red-500 text-xs">{errors.bankBranch}</span>}
         </div>
         <div>
          <label className="block font-medium">IFSC Code:</label>
          <input type="text" name="ifscCode" value={formData.ifscCode} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.ifscCode && <span className="text-red-500 text-xs">{errors.ifscCode}</span>}
         </div>
          Company Declaration
          <span className="tooltip">
          ℹ️
          <span className="tooltiptext">
          Ascentware declares that the bank details provided by the employee will be used solely for salary disbursement & reimbursement, and will be kept confidential as per company policy.
         </span>
         </span>
         </div>
       
          {/* Educational and Professional Details */}
         <h3 className="text-lg font-semibold mb-4 text-blue-600">EDUCATIONAL & PROFESSIONAL DETAILS</h3>
         <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
         <div>
          <label className="block font-medium">Highest Qualification:</label>
          <input type="text" name="highestQualification" value={formData.highestQualification} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.highestQualification && <span className="text-red-500 text-xs">{errors.highestQualification}</span>}
          </div>
          <div> 
          <label className="block font-medium">Do you have any prior experience in IT?</label>
          <div className="flex items-center">
          <input type="radio" name="previousExperience" value="Yes" checked={formData.previousExperience === 'Yes'} onChange={handleChange} className="mr-2" /> Yes
          <input type="radio" name="previousExperience" value="No" checked={formData.previousExperience === 'No'} onChange={handleChange} className="ml-2" /> No
          </div>
          {errors.previousExperience && <span className="text-red-500 text-xs">{errors.previousExperience}</span>}
          </div>
          {formData.previousExperience === 'Yes' && (
          <>
            <div>
              <label className="block font-medium">Previous Employer:</label>
              <input type="text" name="previousEmployer" value={formData.previousEmployer} onChange={handleChange}
                className="mt-1 block w-full rounded border border-gray-300 p-2" />
              {errors.previousEmployer && <span className="text-red-500 text-xs">{errors.previousEmployer}</span>}
            </div>
            <div>
              <label className="block font-medium">Previous Designation:</label>
              <input type="text" name="previousDesignation" value={formData.previousDesignation} onChange={handleChange}
                className="mt-1 block w-full rounded border border-gray-300 p-2" />
              {errors.previousDesignation && <span className="text-red-500 text-xs">{errors.previousDesignation}</span>}
            </div>
            <div>
              <label className="block font-medium">Years of Experience:</label>
              <input type="number" name="yearsOfExperience" value={formData.yearsOfExperience} onChange={handleChange}
                className="mt-1 block w-full rounded border border-gray-300 p-2" />
              {errors.yearsOfExperience && <span className="text-red-500 text-xs">{errors.yearsOfExperience}</span>}
            </div>
          </>
          )}
         </div>

         {/* Joining and Employment Details */}

         <h3 className="text-lg font-semibold mb-4 text-blue-600">JOINING & EMPLOYER DETAILS</h3>

          
         <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
          <label className="block font-medium">Designation:</label>
          <input type="text" name="currentDesignation" value={formData.currentDesignation} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.currentDesignation && <span className="text-red-500 text-xs">{errors.currentDesignation}</span>}
          </div>
          <div>
          <label className="block font-medium">Department:</label>
          <input type="text" name="department" value={formData.department} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.department && <span className="text-red-500 text-xs">{errors.department}</span>}
          </div>
          <div>
          <label className="block font-medium">Employee ID:</label>
          <input type="text" name="employeeId" value={formData.employeeId} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
          {errors.employeeId && <span className="text-red-500 text-xs">{errors.employeeId}</span>}
          </div>
          <div>
          <label className="block font-medium">Date of Joining:</label>
          <input type="date" name="dateOfJoining" value={formData.dateOfJoining} onChange={handleChange}
            className="mt-1 block w-full rounded border border-gray-300 p-2" />
            {errors.dateOfJoining && <span className="text-red-500 text-xs">{errors.dateOfJoining}</span>}
          </div>
         </div>

       

          {/* Documents Submitted */}
          <h3 className="text-lg font-semibold mb-4 text-blue-600">DOCUMENT TO BE SUBMITTED</h3>
         <div>
         <label><input type="checkbox" name="aadhaar" checked={formData.aadhaar} onChange={handleChange} /> Aadhaar/Passport/ID Proof</label><br />
         <label><input type="checkbox" name="panCard" checked={formData.panCard} onChange={handleChange} /> PAN Card</label><br />
         <label><input type="checkbox" name="educationCertificates" checked={formData.educationCertificates} onChange={handleChange} /> Educational Certificates (Degree Certificate with pass status)</label><br />
         <label><input type="checkbox" name="experienceCertificates" checked={formData.experienceCertificates} onChange={handleChange} /> Experience Certificates</label><br />
         <label><input type="checkbox" name="passportPhoto" checked={formData.passportPhoto} onChange={handleChange} /> Personal photo (passport size)</label><br />
         </div>

         {/* Declaration */}
         <h3 className="text-lg font-semibold mb-4 text-blue-600">DECLARATION</h3>
         <div>
          <label>I, {formData.firstName} {formData.lastName}, confirm that the above-provided details are true and accurate. I agree to abide by the company policies and procedures.</label><br />
          <div className="flex items-center gap-5"></div>
         </div>
      
          <label><input type="checkbox" name="agreedTerms" checked={formData.agreedTerms} onChange={handleChange} /> "I Agree" </label>
         {errors.agreedTerms && <span className="text-red-500 text-xs">{errors.agreedTerms}</span>}

         <br />

          <div className="flex items-center gap-2">
  <label htmlFor="declarationDate">Date:</label>
  <input type="date" id="declarationDate" name="declarationDate" value={formData.declarationDate} onChange={handleChange}
    className="mt-1 block w-full rounded border border-gray-300 p-2" />
</div>
      
          <br />
           <div className="flex justify-end mt-8 gap-4">
            <button type="submit" className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">Submit</button>
            <button type="button" onClick={handleReset} className="bg-gray-400 text-white px-6 py-2 rounded hover:bg-gray-500">Reset</button>
          </div>
        </form>
      </div>
    </div>
  ); 
}

export default EmployeeRegistrationForm;
