import React, { useState } from 'react';
import './EmployeeRegistrationForm.css';

function EmployeeRegistrationForm() {
  const initialFormData = {
    // Personal
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    maritalStatus: '',
    contactNumber: '',
    email: '',
    permanentAddress: '',
    currentAddress: '',
    isCurrentAddressSameAsPermanent: false,
    nationality: '',
    hasDisability: '',
    // Work Permit
    workPermitCountry: '',
    workPermitExpiry: '',
    // Emergency
    emergencyContactName: '',
    emergencyRelationship: '',
    emergencyContactNumber: '',
    // Bank
    bankName: '',
    bankBranch: '',
    accountNumber: '',
    ifscCode: '',
    // Education
    highestQualification: '',
    fieldOfStudy: '',
    otherQualification: '',
    // Professional
    previousExperience: '',
    previousEmployer: '',
    previousDesignation: '',
    yearsOfExperience: '',
    // Employment (to be auto-generated by HR)
    currentDesignation: '',
    department: '',
    employeeId: '',
    dateOfJoining: '',
    // Documents
    aadhaar: false,
    panCard: false,
    educationCertificates: false,
    experienceCertificates: false,
    passportPhoto: false,
    // Declaration
    agreedTerms: false,
    declarationDate: '',
  };

  const [formData, setFormData] = useState(initialFormData);
  const [errors, setErrors] = useState({});
  const [fadingErrors, setFadingErrors] = useState({}); // For fade-out animation

  // Helper to get the "final" highest qualification for backend
  const getFinalQualification = () => {
    if (formData.highestQualification === "Other") {
      return formData.otherQualification;
    }
    return formData.highestQualification;
  };

  // Fade out error on input
  const triggerFadeOut = (name) => {
    if (errors[name]) {
      setFadingErrors((prev) => ({ ...prev, [name]: true }));
      setTimeout(() => {
        setFadingErrors((prev) => ({ ...prev, [name]: false }));
        setErrors((prevErrors) => ({ ...prevErrors, [name]: undefined }));
      }, 500);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    triggerFadeOut(name);

    if (type === 'checkbox') {
      setFormData((prev) => {
        const next = { ...prev, [name]: checked };
        if (name === 'isCurrentAddressSameAsPermanent') {
          next.currentAddress = checked ? prev.permanentAddress : '';
        }
        if (name === 'agreedTerms' && checked) {
          alert('By checking the box you are agreeing to the Terms & Conditions and Privacy policy.');
        }
        return next;
      });
    } else {
      setFormData((prev) => {
        let updated = { ...prev, [name]: value };
        if (name === 'permanentAddress' && prev.isCurrentAddressSameAsPermanent) {
          updated.currentAddress = value;
        }
        return updated;
      });
    }
  };

  const validate = () => {
    let validationErrors = {};

    // Personal Details
    if (!(formData.firstName || '').trim()) {
      validationErrors.firstName = "First name is required.";
    } else if (!/^[A-Za-z\s]+$/.test(formData.firstName)) {
      validationErrors.firstName = "First name must contain only letters.";
    }

    if (formData.middleName && !/^[A-Za-z\s]+$/.test(formData.middleName)) {
      validationErrors.middleName = "Middle name must contain only letters.";
    }

    if (!(formData.lastName || '').trim()) {
      validationErrors.lastName = "Last name is required.";
    } else if (!/^[A-Za-z\s]+$/.test(formData.lastName)) {
      validationErrors.lastName = "Last name must contain only letters.";
    }

    if (!formData.dateOfBirth) validationErrors.dateOfBirth = "Date of birth is required.";

    if (!formData.email || !String(formData.email).match(/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/)) {
      validationErrors.email = "Enter a valid email address.";
    }

    if (!formData.contactNumber || !String(formData.contactNumber).match(/^\d{10}$/)) {
      validationErrors.contactNumber = "Enter a valid 10-digit contact number.";
    }

    if (!formData.gender) validationErrors.gender = "Gender is required.";
    if (!formData.maritalStatus) validationErrors.maritalStatus = "Marital status is required.";
    if (formData.hasDisability === "yes" && !(formData.disabilityDetails || '').trim()) validationErrors.disabilityDetails = "Disability details are required.";
    if (!(formData.permanentAddress || '').trim()) validationErrors.permanentAddress = "Permanent address is required.";
    if (!(formData.currentAddress || '').trim()) validationErrors.currentAddress = "Current address is required.";
    if (!(formData.nationality || '').trim()) validationErrors.nationality = "Nationality is required.";

    // Work Permit
    if (!(formData.workPermitCountry || '').trim()) validationErrors.workPermitCountry = "Work permit country is required.";
    if (!formData.workPermitExpiry) validationErrors.workPermitExpiry = "Work permit expiry date is required.";

    // Emergency Contacts
    if (!(formData.emergencyContactName || '').trim()) {
      validationErrors.emergencyContactName = "Emergency contact name is required.";
    } else if (!/^[A-Za-z\s]+$/.test(formData.emergencyContactName)) {
      validationErrors.emergencyContactName = "Must contain only letters.";
    }
    if (!(formData.emergencyRelationship || '').trim()) {
      validationErrors.emergencyRelationship = "Emergency contact relationship is required.";
    } else if (!/^[A-Za-z\s]+$/.test(formData.emergencyRelationship)) {
      validationErrors.emergencyRelationship = "Must contain only letters.";
    }
    if (!formData.emergencyContactNumber || !String(formData.emergencyContactNumber).match(/^\d{10}$/)) {
      validationErrors.emergencyContactNumber = "Enter a valid 10-digit emergency contact number.";
    }

    // Bank Details
    if (!(formData.bankName || '').trim()) {
      validationErrors.bankName = "Bank name is required.";
    } else if (!/^[A-Za-z\s]+$/.test(formData.bankName)) {
      validationErrors.bankName = "Bank name must contain only letters.";
    }
    if (!(formData.bankBranch || '').trim()) {
      validationErrors.bankBranch = "Branch is required.";
    } else if (!/^[A-Za-z\s]+$/.test(formData.bankBranch)) {
      validationErrors.bankBranch = "Branch name must contain only letters.";
    }
    if (!formData.accountNumber || !String(formData.accountNumber).match(/^\d{9,18}$/)) validationErrors.accountNumber = "Enter a valid account number.";
    if (!formData.ifscCode || !String(formData.ifscCode).match(/^[A-Z]{4}0[A-Z0-9]{6}$/)) validationErrors.ifscCode = "Enter a valid IFSC code.";

    // Education
    if (!(formData.highestQualification || '').trim()) validationErrors.highestQualification = "Highest qualification is required.";
    if (!formData.fieldOfStudy || !(formData.fieldOfStudy || '').trim()) validationErrors.fieldOfStudy = "Field of study is required.";

    // Professional
    if (!formData.previousExperience) validationErrors.previousExperience = "Previous experience is required.";
    if (formData.previousExperience === "Yes") {
      if (!(formData.previousEmployer || '').trim()) validationErrors.previousEmployer = "Previous employer is required.";
      if (!(formData.previousDesignation || '').trim()) validationErrors.previousDesignation = "Previous designation is required.";
      if (!(formData.yearsOfExperience || '').trim()) validationErrors.yearsOfExperience = "Years of experience is required.";
    }

    // Declaration
    if (!formData.agreedTerms) validationErrors.agreedTerms = "You must agree to terms and conditions.";
    if (!formData.declarationDate) validationErrors.declarationDate = "Declaration date is required.";

    return validationErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    setErrors(validationErrors);
    setFadingErrors({}); // Reset fade-outs on submit
    if (Object.keys(validationErrors).length > 0) {
      // Scroll to first error field
      const firstErrorField = document.querySelector('.input-error');
      if (firstErrorField) {
        firstErrorField.focus();
      }
      alert("Please correct the highlighted errors.");
      return;
    }
    // Prepare data for submission
    const submissionData = { ...formData };
    submissionData.highestQualification = getFinalQualification();

    try {
      const response = await fetch("http://localhost:8070", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(submissionData),
      });
      if (!response.ok) throw new Error(`Error: ${response.statusText}`);
      await response.json();
      alert("Form submitted successfully!");
      setFormData(initialFormData);
      setErrors({});
      setFadingErrors({});
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("Failed to submit the form. Please try again later.");
    }
  };

  const handleReset = () => {
    if (window.confirm("Are you sure to reset the form?")) {
      setFormData(initialFormData);
      setErrors({});
      setFadingErrors({});
      alert("Form has been reset!");
    }
  };

  return (
    <div className="form-container">
      <img src="https://www.ascentware.in/images/logo.png" alt="Company Logo" className="company-logo" />
      <div className="form-inner" style={{ backgroundColor: 'aliceblue' }}>
        <h2>EMPLOYEE REGISTRATION</h2>
        <form onSubmit={handleSubmit} autoComplete="off">
          {/* PERSONAL DETAILS */}
          <h3>PERSONAL DETAILS</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>First Name:</label>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                className={errors.firstName ? "input-error" : formData.firstName ? "input-valid" : ""}
              />
              {errors.firstName &&
                <span className={`error${fadingErrors.firstName ? " fade-out" : ""}`}>
                  {errors.firstName}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Middle Name (Optional):</label>
              <input
                type="text"
                name="middleName"
                value={formData.middleName}
                onChange={handleChange}
                className={errors.middleName ? "input-error" : formData.middleName ? "input-valid" : ""}
              />
              {errors.middleName &&
                <span className={`error${fadingErrors.middleName ? " fade-out" : ""}`}>
                  {errors.middleName}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Last Name:</label>
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                className={errors.lastName ? "input-error" : formData.lastName ? "input-valid" : ""}
              />
              {errors.lastName &&
                <span className={`error${fadingErrors.lastName ? " fade-out" : ""}`}>
                  {errors.lastName}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Date of Birth:</label>
              <input
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
                className={errors.dateOfBirth ? "input-error" : formData.dateOfBirth ? "input-valid" : ""}
                min="1960-01-01"
                max="2025-01-01"
              />
              {errors.dateOfBirth &&
                <span className={`error${fadingErrors.dateOfBirth ? " fade-out" : ""}`}>
                  {errors.dateOfBirth}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Gender:</label><br />
              <input type="radio" name="gender" value="Male" checked={formData.gender === 'Male'} onChange={handleChange} /> Male
              <input type="radio" name="gender" value="Female" checked={formData.gender === 'Female'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Female
              <input type="radio" name="gender" value="Other" checked={formData.gender === 'Other'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Other
              {errors.gender &&
                <span className={`error${fadingErrors.gender ? " fade-out" : ""}`}>
                  {errors.gender}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Marital Status:</label><br />
              <input type="radio" name="maritalStatus" value="Single" checked={formData.maritalStatus === 'Single'} onChange={handleChange} /> Single
              <input type="radio" name="maritalStatus" value="Married" checked={formData.maritalStatus === 'Married'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Married
              <input type="radio" name="maritalStatus" value="Others" checked={formData.maritalStatus === 'Others'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Others
              {errors.maritalStatus &&
                <span className={`error${fadingErrors.maritalStatus ? " fade-out" : ""}`}>
                  {errors.maritalStatus}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Contact Number:</label>
              <input
                type="tel"
                name="contactNumber"
                value={formData.contactNumber}
                onChange={handleChange}
                className={errors.contactNumber ? "input-error" : formData.contactNumber ? "input-valid" : ""}
              />
              {errors.contactNumber &&
                <span className={`error${fadingErrors.contactNumber ? " fade-out" : ""}`}>
                  {errors.contactNumber}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Email ID:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="abc@xyz.com"
                className={errors.email ? "input-error" : formData.email ? "input-valid" : ""}
              />
              {errors.email &&
                <span className={`error${fadingErrors.email ? " fade-out" : ""}`}>
                  {errors.email}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Permanent Address:</label>
              <textarea
                name="permanentAddress"
                value={formData.permanentAddress}
                onChange={handleChange}
                className={errors.permanentAddress ? "input-error" : formData.permanentAddress ? "input-valid" : ""}
              />
              {errors.permanentAddress &&
                <span className={`error${fadingErrors.permanentAddress ? " fade-out" : ""}`}>
                  {errors.permanentAddress}
                </span>
              }
            </div>
            <div className="form-group" style={{ display: 'flex', alignItems: 'center' }}>
              <label style={{ marginRight: '10px' }}>Is the current address same as permanent address?</label>
              <input
                type="checkbox"
                name="isCurrentAddressSameAsPermanent"
                checked={formData.isCurrentAddressSameAsPermanent}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Current Address:</label>
              <textarea
                name="currentAddress"
                value={formData.currentAddress}
                onChange={handleChange}
                disabled={formData.isCurrentAddressSameAsPermanent}
                className={errors.currentAddress ? "input-error" : formData.currentAddress ? "input-valid" : ""}
              />
              {errors.currentAddress &&
                <span className={`error${fadingErrors.currentAddress ? " fade-out" : ""}`}>
                  {errors.currentAddress}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Nationality:</label>
              <input
                type="text"
                name="nationality"
                value={formData.nationality}
                onChange={handleChange}
                className={errors.nationality ? "input-error" : formData.nationality ? "input-valid" : ""}
              />
              {errors.nationality &&
                <span className={`error${fadingErrors.nationality ? " fade-out" : ""}`}>
                  {errors.nationality}
                </span>
              }
            </div>
          </div>
          <div className="form-group full-width">
            <label>PWD (Person with Disability) Status</label>
            <div>
              <label>
                <input type="radio" name="hasDisability" value="yes" checked={formData.hasDisability === "yes"} onChange={handleChange} /> Yes
              </label>
              <label style={{ marginLeft: "10px" }}>
                <input type="radio" name="hasDisability" value="no" checked={formData.hasDisability === "no"} onChange={handleChange} /> No
              </label>
            </div>
          </div>
          {formData.hasDisability === "yes" && (
            <div className="form-group full-width" style={{ color: "red", fontWeight: "bold" }}>
              If Yes (Request you to share the PWD documents with details and % of disability)
            </div>
          )}

          {/* WORK PERMIT DETAILS */}
          <h3>WORK PERMIT DETAILS</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Work Permit Available Country:</label>
              <input
                type="text"
                name="workPermitCountry"
                value={formData.workPermitCountry}
                onChange={handleChange}
                className={errors.workPermitCountry ? "input-error" : formData.workPermitCountry ? "input-valid" : ""}
              />
              {errors.workPermitCountry &&
                <span className={`error${fadingErrors.workPermitCountry ? " fade-out" : ""}`}>
                  {errors.workPermitCountry}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Work Permit Expiry Date:</label>
              <input
                type="date"
                name="workPermitExpiry"
                value={formData.workPermitExpiry}
                onChange={handleChange}
                className={errors.workPermitExpiry ? "input-error" : formData.workPermitExpiry ? "input-valid" : ""}
              />
              {errors.workPermitExpiry &&
                <span className={`error${fadingErrors.workPermitExpiry ? " fade-out" : ""}`}>
                  {errors.workPermitExpiry}
                </span>
              }
            </div>
          </div>

          {/* EMERGENCY CONTACTS */}
          <h3>EMERGENCY CONTACT DETAILS</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Emergency Contact Name:</label>
              <input
                type="text"
                name="emergencyContactName"
                value={formData.emergencyContactName}
                onChange={handleChange}
                className={errors.emergencyContactName ? "input-error" : formData.emergencyContactName ? "input-valid" : ""}
              />
              {errors.emergencyContactName &&
                <span className={`error${fadingErrors.emergencyContactName ? " fade-out" : ""}`}>
                  {errors.emergencyContactName}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Relationship:</label>
              <input
                type="text"
                name="emergencyRelationship"
                value={formData.emergencyRelationship}
                onChange={handleChange}
                className={errors.emergencyRelationship ? "input-error" : formData.emergencyRelationship ? "input-valid" : ""}
              />
              {errors.emergencyRelationship &&
                <span className={`error${fadingErrors.emergencyRelationship ? " fade-out" : ""}`}>
                  {errors.emergencyRelationship}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Emergency Contact Number:</label>
              <input
                type="tel"
                name="emergencyContactNumber"
                value={formData.emergencyContactNumber}
                onChange={handleChange}
                className={errors.emergencyContactNumber ? "input-error" : formData.emergencyContactNumber ? "input-valid" : ""}
              />
              {errors.emergencyContactNumber &&
                <span className={`error${fadingErrors.emergencyContactNumber ? " fade-out" : ""}`}>
                  {errors.emergencyContactNumber}
                </span>
              }
            </div>
          </div>

          {/* BANK DETAILS */}
          <h3>BANK & SALARY DETAILS</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Bank Name:</label>
              <input
                type="text"
                name="bankName"
                value={formData.bankName}
                onChange={handleChange}
                className={errors.bankName ? "input-error" : formData.bankName ? "input-valid" : ""}
              />
              {errors.bankName &&
                <span className={`error${fadingErrors.bankName ? " fade-out" : ""}`}>
                  {errors.bankName}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Branch:</label>
              <input
                type="text"
                name="bankBranch"
                value={formData.bankBranch}
                onChange={handleChange}
                className={errors.bankBranch ? "input-error" : formData.bankBranch ? "input-valid" : ""}
              />
              {errors.bankBranch &&
                <span className={`error${fadingErrors.bankBranch ? " fade-out" : ""}`}>
                  {errors.bankBranch}
                </span>
              }
            </div>
            <div className="form-group">
              <label>Account Number:</label>
              <input
                type="text"
                name="accountNumber"
                value={formData.accountNumber}
                onChange={handleChange}
                className={errors.accountNumber ? "input-error" : formData.accountNumber ? "input-valid" : ""}
              />
              {errors.accountNumber &&
                <span className={`error${fadingErrors.accountNumber ? " fade-out" : ""}`}>
                  {errors.accountNumber}
                </span>
              }
            </div>
            <div className="form-group">
              <label>IFSC Code:</label>
              <input
                type="text"
                name="ifscCode"
                value={formData.ifscCode}
                onChange={handleChange}
                className={errors.ifscCode ? "input-error" : formData.ifscCode ? "input-valid" : ""}
              />
              {errors.ifscCode &&
                <span className={`error${fadingErrors.ifscCode ? " fade-out" : ""}`}>
                  {errors.ifscCode}
                </span>
              }
            </div>
            <div className="form-group full-width">
              Declaration
              <span className="tooltip">
                
                <span className="tooltiptext">
                  Ascentware declares that the bank details provided by the employee will be used solely for salary disbursement & reimbursement, and will be kept confidential as per company policy.
                </span>
              </span>
            </div>
          </div>

          {/* EDUCATION DETAILS */}
          <h3>EDUCATIONAL DETAILS</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Highest Qualification:</label>
              <select
                name="highestQualification"
                value={formData.highestQualification}
                onChange={handleChange}
                className={errors.highestQualification ? "input-error" : formData.highestQualification ? "input-valid" : ""}
              >
                <option value="">-- Select Qualification --</option>
                <option value="High School">High School</option>
                <option value="Diploma">Diploma</option>
                <option value="Bachelor's Degree">Bachelor's Degree</option>
                <option value="Master's Degree">Master's Degree</option>
                <option value="Doctorate/Ph.D.">Doctorate/Ph.D.</option>
                <option value="Other">Other (Please specify below)</option>
              </select>
              {errors.highestQualification &&
                <span className={`error${fadingErrors.highestQualification ? " fade-out" : ""}`}>
                  {errors.highestQualification}
                </span>
              }
              {formData.highestQualification === "Other" && (
                <>
                  <input
                    type="text"
                    name="otherQualification"
                    value={formData.otherQualification}
                    onChange={handleChange}
                    placeholder="Please specify your highest qualification"
                    className={errors.otherQualification ? "input-error" : ""}
                  />
                  {errors.otherQualification &&
                    <span className={`error${fadingErrors.otherQualification ? " fade-out" : ""}`}>
                      {errors.otherQualification}
                    </span>
                  }
                </>
              )}
            </div>
            <div className="form-group">
              <label>Field of Study:</label>
              <input
                type="text"
                name="fieldOfStudy"
                value={formData.fieldOfStudy}
                onChange={handleChange}
                placeholder="e.g. Mechanical Engineering, Computer Science, Commerce"
                className={errors.fieldOfStudy ? "input-error" : formData.fieldOfStudy ? "input-valid" : ""}
              />
              {errors.fieldOfStudy &&
                <span className={`error${fadingErrors.fieldOfStudy ? " fade-out" : ""}`}>
                  {errors.fieldOfStudy}
                </span>
              }
            </div>
          </div>

          {/* PROFESSIONAL DETAILS */}
          <h3>PROFESSIONAL DETAILS</h3>
          <div className="form-group" style={{ display: "flex", gap: "24px" }}>
            <label style={{ marginRight: "12px" }}>
              Do you have any prior experience in IT?
            </label>
            <label style={{ display: "flex", gap: "4px" }}>
              <input
                type="radio"
                name="previousExperience"
                value="Yes"
                checked={formData.previousExperience === "Yes"}
                onChange={handleChange}
              />
              Yes
            </label>
            <label style={{ display: "flex", gap: "4px" }}>
              <input
                type="radio"
                name="previousExperience"
                value="No"
                checked={formData.previousExperience === "No"}
                onChange={handleChange}
              />
              No
            </label>
            {errors.previousExperience &&
              <span className={`error${fadingErrors.previousExperience ? " fade-out" : ""}`} style={{ marginLeft: 8 }}>
                {errors.previousExperience}
              </span>
            }
          </div>
          {formData.previousExperience === 'Yes' && (
            <div className="form-grid">
              <div className="form-group">
                <label>Previous Employer:</label>
                <input
                  type="text"
                  name="previousEmployer"
                  value={formData.previousEmployer}
                  onChange={handleChange}
                  className={errors.previousEmployer ? "input-error" : formData.previousEmployer ? "input-valid" : ""}
                />
                {errors.previousEmployer &&
                  <span className={`error${fadingErrors.previousEmployer ? " fade-out" : ""}`}>
                    {errors.previousEmployer}
                  </span>
                }
              </div>
              <div className="form-group">
                <label>Previous Designation:</label>
                <input
                  type="text"
                  name="previousDesignation"
                  value={formData.previousDesignation}
                  onChange={handleChange}
                  className={errors.previousDesignation ? "input-error" : formData.previousDesignation ? "input-valid" : ""}
                />
                {errors.previousDesignation &&
                  <span className={`error${fadingErrors.previousDesignation ? " fade-out" : ""}`}>
                    {errors.previousDesignation}
                  </span>
                }
              </div>
              <div className="form-group">
                <label>Years of Experience:</label>
                <input
                  type="number"
                  name="yearsOfExperience"
                  value={formData.yearsOfExperience}
                  onChange={handleChange}
                  className={errors.yearsOfExperience ? "input-error" : formData.yearsOfExperience ? "input-valid" : ""}
                />
                {errors.yearsOfExperience &&
                  <span className={`error${fadingErrors.yearsOfExperience ? " fade-out" : ""}`}>
                    {errors.yearsOfExperience}
                  </span>
                }
              </div>
            </div>
          )}

          {/* JOINING AND EMPLOYER DETAILS (Auto-generated by HR) */}
          <h3>JOINING & EMPLOYER DETAILS</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Designation: <span style={{color: "#888"}}>(To be filled by HR)</span></label>
              <input
                type="text"
                name="currentDesignation"
                value={formData.currentDesignation}
                readOnly
                disabled
                placeholder="Auto-generated by HR"
              />
            </div>
            <div className="form-group">
              <label>Department: <span style={{color: "#888"}}>(To be filled by HR)</span></label>
              <input
                type="text"
                name="department"
                value={formData.department}
                readOnly
                disabled
                placeholder="Auto-generated by HR"
              />
            </div>
            <div className="form-group">
              <label>Employee ID: <span style={{color: "#888"}}>(To be filled by HR)</span></label>
              <input
                type="text"
                name="employeeId"
                value={formData.employeeId}
                readOnly
                disabled
                placeholder="Auto-generated by HR"
              />
            </div>
            <div className="form-group">
              <label>Date of Joining: <span style={{color: "#888"}}>(To be filled by HR)</span></label>
              <input
                type="date"
                name="dateOfJoining"
                value={formData.dateOfJoining}
                readOnly
                disabled
                placeholder="Auto-generated by HR"
              />
            </div>
          </div>

          {/* DECLARATION */}
          <h3>DECLARATION</h3>
          <div className="form-group">
            <label>
              I hereby declare that the information provided above is true to the best of my knowledge and belief. I understand that any false information may lead to termination of employment.
            </label>
            <label>
              <input
                type="checkbox"
                name="agreedTerms"
                checked={formData.agreedTerms}
                onChange={handleChange}
              />{" "}
              "I Agree"
            </label>
            {errors.agreedTerms &&
              <span className={`error${fadingErrors.agreedTerms ? " fade-out" : ""}`}>
                {errors.agreedTerms}
              </span>
            }
          </div>
          
          <div className="form-group" style={{ maxWidth: 200 }}>
            <label htmlFor="declarationDate">Date:</label>
            <input
              type="date"
              id="declarationDate"
              name="declarationDate"
              value={formData.declarationDate}
              onChange={handleChange}
              className={errors.declarationDate ? "input-error" : formData.declarationDate ? "input-valid" : ""}
              style={{ width: "100%" }}
            />
            {errors.declarationDate &&
              <span className={`error${fadingErrors.declarationDate ? " fade-out" : ""}`}>
                {errors.declarationDate}
              </span>
            }
          </div>
          <div>
            <button type="submit">Submit</button>
            <button type="button" onClick={handleReset} style={{ marginLeft: '10px' }}>Reset</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EmployeeRegistrationForm;
