import React, { useState } from 'react';

import './EmployeeRegistrationForm.css';

function EmployeeRegistrationForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    dateOfBirth: '',
    gender: '',
    maritalStatus: '',
    contactNumber: '',
    email: '',
    permanentAddress: '',
    currentAddress: '',
    hasDisability: '',
    previousExperience: '',
    nationality: '',
    workPermitCountry: '',
    workPermitExpiry: '',
    emergencyContactName: '',
    emergencyRelationship: '',
    emergencyContactNumber: '',
    bankName: '',
    bankBranch: '',
    accountNumber: '',
    ifscCode: '',
    highestQualification: '',
    previousEmployer: '',
    previousDesignation: '',
    yearsOfExperience: '',
    currentDesignation: '',
    department: '',
    employeeId: '',
    dateOfJoining: '',
    aadhaar: false,
    panCard: false,
    educationCertificates: false,
    experienceCertificates: false,
    passportPhoto: false,
    declarationAccepted: false,
    agreedTerms: false,
    declarationDate: '',
    isCurrentAddressSameAsPermanent: false
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setFormData((prev) => ({ ...prev, [name]: checked }));
    } else if (type === 'file') {
      setFormData((prev) => ({ ...prev, [name]: files[0] }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    //personal
    let validationErrors = {};
    if (!formData.firstName.trim()) validationErrors.firstName = "First name is required.";
    if (!formData.lastName.trim()) validationErrors.lastName = "Last name is required.";
    if (!formData.dateOfBirth) validationErrors.dateOfBirth = "Date of birth is required.";
    if (!formData.gender) validationErrors.gender = "Gender is required.";
    if (!formData.maritalStatus) validationErrors.maritalStatus = "Marital status is required.";
    if (!formData.contactNumber.match(/^\d{10}$/)) validationErrors.contactNumber = "Enter a valid 10-digit contact number.";
    if (!formData.email.match(/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/)) validationErrors.email = "Enter a valid email address.";
    if (!formData.permanentAddress.trim()) validationErrors.permanentAddress = "Permanent address is required.";
    if (!formData.nationality.trim()) validationErrors.nationality = "Nationality is required.";
    if (!formData.declarationAccepted) validationErrors.declarationAccepted = "Please confirm your declaration.";
    if (!formData.agreedTerms) validationErrors.agreedTerms = "You must agree to terms and conditions.";


   // ✅ Bank Info
   if (!formData.bankName.trim()) validationErrors.bankName = "Bank name is required.";
   if (!formData.bankBranch.trim()) validationErrors.bankBranch = "Branch is required.";
   if (!formData.accountNumber.match(/^\d{9,18}$/)) validationErrors.accountNumber = "Enter a valid account number.";
   if (!formData.ifscCode.match(/^[A-Z]{4}0[A-Z0-9]{6}$/)) validationErrors.ifscCode = "Enter a valid IFSC code.";

   // ✅ Job Info
    if (!formData.currentDesignation.trim()) validationErrors.currentDesignation = "Current designation is required.";
    if (!formData.department.trim()) validationErrors.department = "Department is required.";
    if (!formData.employeeId.trim()) validationErrors.employeeId = "Employee ID is required.";
    if (!formData.dateOfJoining) validationErrors.dateOfJoining = "Date of joining is required.";

  // Document validation
   if (!formData.aadhaar) validationErrors.aadhaar = "Aadhaar/ID proof is required.";
   if (!formData.panCard) validationErrors.panCard = "PAN Card is required.";
   if (!formData.educationCertificates) validationErrors.educationCertificates = "Educational certificates are required.";
   if (!formData.experienceCertificates) validationErrors.experienceCertificates = "Experience certificates are required.";
   if (!formData.passportPhoto) validationErrors.passportPhoto = "Passport size photo is required.";
   if (!formData.signatureFile) validationErrors.signatureFile = "Signature is required.";


    // ✅ Declaration
    if (!formData.declarationAccepted) validationErrors.declarationAccepted = "Please confirm your declaration.";
    if (!formData.agreedTerms) validationErrors.agreedTerms = "You must agree to terms and conditions.";
    if (!formData.declarationDate) validationErrors.declarationDate = "Declaration date is required.";
    


    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      alert("Please correct the highlighted errors.");
      return;
    }

    console.log('Submitted Data:', formData);
    alert('Form submitted successfully!');
    setErrors({});

    const handleSubmit = (e) => {
      e.preventDefault();
      const allFilled = Object.values(formData).every(field => field.trim() !== '');

    if (allFilled) {
      alert('Form submitted successfully!');
      // Here, you can also send the data to a backend or clear the form
    } else {
      alert('Please fill all the fields before submitting.');
    }

  };
};
  const handleReset = () => {
    const confirmReset = window.confirm("Are you sure to reset the form?");
    if (confirmReset) {
      setFormData({
        firstName: '',
        middleName: '',
        lastName: '',
        dateOfBirth: '',
        gender: '',
        maritalStatus: '',
        contactNumber: '',
        email: '',
        permanentAddress: '',
        currentAddress: '',
        hasDisability: '',
        previousExperience: '',
        nationality: '',
        workPermitCountry: '',
        workPermitExpiry: '',
        emergencyContactName: '',
        emergencyRelationship: '',
        emergencyContactNumber: '',
        bankName: '',
        bankBranch: '',
        accountNumber: '',
        ifscCode: '',
        highestQualification: '',
        previousEmployer: '',
        previousDesignation: '',
        yearsOfExperience: '',
        currentDesignation: '',
        department: '',
        employeeId: '',
        dateOfJoining: '',
        aadhaar: false,
        panCard: false,
        educationCertificates: false,
        experienceCertificates: false,
        passportPhoto: false,
        declarationAccepted: false,
        agreedTerms: false,
        declarationDate: '',
        isCurrentAddressSameAsPermanent: false
      });
      setErrors({});
      alert("Form has been reset!");
    }
  };

  return (
    <div className="form-container">
      <img
        src="https://www.ascentware.in/images/logo.png"
        alt="Company Logo"
        className="company-logo"
      />

      <div style={{ maxWidth: '600px', margin: 'auto' }}>
        <h2>EMPLOYEE REGISTRATION</h2>
        <h3>PERSONAL DETAILS</h3>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label>First Name:</label><br />
              <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} 
                className={errors.firstName? "input-error": formData.firstName? "input-valid": "" }/>

              {errors.firstName && <span className="error">{errors.firstName}</span>}
            </div>
            <label>Middle Name(Optional):</label><br />
            <input type="text" name="middleName" value={formData.middleName} onChange={handleChange}/>

            <div>
              <label>Last Name:</label><br />
              <input type="text" name="lastName" value={formData.lastName} onChange={handleChange}/>
              {errors.lastName && <span className="error">{errors.lastName}</span>}
            </div>
            <div>
              <label>Date of Birth:</label><br />
              <input type="date" name="dateOfBirth" value={formData.dateOfBirth} onChange={handleChange} required min="1995-01-01" max="2025-01-01"/>
              {errors.dateOfBirth && <span className="error">{errors.dateOfBirth}</span>}
            </div>
            <div>
              <label>Gender:</label><br />
              <input type="radio" name="gender" value="Male" checked={formData.gender === 'Male'} onChange={handleChange} /> Male
              <input type="radio" name="gender" value="Female" checked={formData.gender === 'Female'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Female
              <input type="radio" name="gender" value="Other" checked={formData.gender === 'Other'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Other
              {errors.gender && <span className="error">{errors.gender}</span>}
            </div>
            <div>
              <label>Marital Status:</label><br />
              <input type="radio" name="maritalStatus" value="Single" checked={formData.maritalStatus === 'Single'} onChange={handleChange} /> Single
              <input type="radio" name="maritalStatus" value="Married" checked={formData.maritalStatus === 'Married'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Married
              <input type="radio" name="maritalStatus" value="Others" checked={formData.maritalStatus === 'Others'} onChange={handleChange} style={{ marginLeft: '10px' }} /> Others
              {errors.maritalStatus && <span className="error">{errors.maritalStatus}</span>}
            </div>
            <div>
                <label>Contact Number:</label><br />
                <input type="tel"  name="contactNumber" value={formData.contactNumber} onChange={handleChange}
                placeholder="+91 9876543210"
                 style={{ width: '100%', padding: '8px', fontSize: '16px' }} />
                {errors.contactNumber && <span className="error">{errors.contactNumber}</span>}
            </div>

            <div>
              <label>Email ID:</label><br />
            <input type="email"  name="email"  value={formData.email}  onChange={handleChange} placeholder="Email" 
             className={errors.email ? "input-error": formData.email? "input-valid": ""}/>



            
              {/* <input type="email" name="email" value={formData.email} onChange={handleChange} />
              {errors.email && <span className="error">{errors.email}</span>} */}
            </div>
            <div>
              <label>Permanent Address:</label><br />
              <textarea name="permanentAddress" value={formData.permanentAddress} onChange={handleChange}/>
              {errors.permanentAddress && <span className="error">{errors.permanentAddress}</span>}
            </div>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <label style={{ marginRight: '10px' }}>Is the current address same as permanent address?</label>
              <input type="checkbox" name="isCurrentAddressSameAsPermanent" checked={formData.isCurrentAddressSameAsPermanent} 
              onChange={handleChange}/>
            </div>
       


            <div>
              <label>Current Address:</label><br />
              <textarea name="currentAddress" value={formData.currentAddress} onChange={handleChange} disabled={formData.isCurrentAddressSameAsPermanent} />
             
            </div>
            

            {/* <div>
              <label>Current Address:</label><br />
              <textarea name="currentAddress" value={formData.currentAddress} onChange={handleChange}/>
              {errors.currentAddress && <span className="error">{errors.currentAddress}</span>}
            </div> */}
            <div>
              <label>Nationality:</label><br />
              <input type="text" name="nationality" value={formData.nationality} onChange={handleChange}/>
              {errors.nationality && <span className="error">{errors.nationality}</span>}
            </div>
          </div>

      

          <div className="full-width">
             <label>PWD(Person with Disability) Status</label>
            {/* </div> <div style={{ display: "flex", gap: "20px", marginTop: "5px" }}> */}
             <div>
              <label>
              <input type="radio" name="hasDisability" value="yes" checked={formData.hasDisability === "yes"}
               onChange={handleChange}/>{" "} Yes
            </label>
            <label style={{ marginLeft: "1px" }}>
             <input type="radio" name="hasDisability" value="no" checked={formData.hasDisability === "no"}
             onChange={handleChange}/>{" "}No
            </label>
          </div>
        </div>

      {/* Conditional Message */}
      {formData.hasDisability === "yes" && (
        <div className="full-width" style={{ color: "red", fontWeight: "bold" }}>
          Please provide a valid document for the disability.
        </div>
      )}


        <div>
          <label>Work Permit Available Country:</label><br />
          <input type="text" name="workPermitCountry" value={formData.workPermitCountry} onChange={handleChange} />
        </div>

        <div>
          <label>Work Permit Expiry Date:</label><br />
          <input type="date" name="workPermitExpiry" value={formData.workPermitExpiry} onChange={handleChange} />
        </div>
        
        {/* EMERGENCY CONTACTS */}

        <h3>EMERGENCY CONTACT DETAILS</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label>Emergency Contact Name:</label><br />
          <input type="text" name="emergencyContactName" value={formData.emergencyContactName} onChange={handleChange} />
          {errors.emergencyContactName && <span className="error">{errors.emergencyContactName}</span>}
        </div>
        <div>
          <label>Relationship:</label><br />
          <input type="text" name="emergencyRelationship" value={formData.emergencyRelationship} onChange={handleChange} />
          {errors.emergencyRelationship && <span className="error">{errors.emergencyRelationship}</span>}
        </div>
        <div>
           <label>Emergency Contact Number:</label><br />
            <input type="tel"  name="emergencyContactNumber"  value={formData.emergencyContactNumber} onChange={handleChange}
              placeholder="+91 9876543210"
             style={{ width: '100%', padding: '8px', fontSize: '16px' }} 
             />
            {errors.emergencyContactNumber && <span className="error">{errors.emergencyContactNumber}</span>}
        </div>
      </div>

       {/* Bank and Salary Details */}

       <h3>BANK & SALARY DETAILS</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label>Bank Name:</label><br />
          <input type="text" name="bankName" value={formData.bankName} onChange={handleChange} />
          {errors.nationality && <span bankName="error">{errors.bankName}</span>}
        </div>
        <div>
          <label>Branch:</label><br />
          <input type="text" name="bankBranch" value={formData.bankBranch} onChange={handleChange} />
          {errors.bankBranch && <span className="error">{errors.bankBranch}</span>}
        </div>
        <div>
          <label>Account Number:</label><br />
          <input type="text" name="accountNumber" value={formData.accountNumber} onChange={handleChange} />
        </div>
        <div>
          <label>IFSC Code:</label><br />
          <input type="text" name="ifscCode" value={formData.ifscCode} onChange={handleChange} />
          {errors.ifscCode && <span className="error">{errors.ifscCode}</span>}
        </div>
        Company Declaration
      <span className="tooltip">
       ℹ️
       <span className="tooltiptext">
        Ascentware declares that the bank details provided by the employee will be used solely for salary disbursement & reimbursement, and will be kept confidential as per company policy.
       </span>
       </span>
       </div>
       
      {/* Educational and Professional Details */}
      <h3>EDUCATIONAL & PROFESSIONAL DETAILS</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label>Highest Qualification:</label><br />
          <input type="text" name="highestQualification" value={formData.highestQualification} onChange={handleChange} />
          {errors.highestQualification && <span className="error">{errors.highestQualification}</span>}
        </div>
        <div> 
          <label>Do you have experience prior to Ascentware?</label><br />
          <input type="radio" name="previousExperience" value="Yes" checked={formData.previousExperience === 'Yes'} onChange={handleChange} /> Yes
          <input type="radio" name="previousExperience" value="No" checked={formData.previousExperience === 'No'} onChange={handleChange} style={{ marginLeft: '10px' }} /> No
          {errors.previousExperience && <span className="error">{errors.previousExperience}</span>}
        </div>
        {formData.previousExperience === 'Yes' && (
          <>
            <div>
              <label>Previous Employer:</label><br />
              <input type="text" name="previousEmployer" value={formData.previousEmployer} onChange={handleChange} />
              {errors.previousEmployer && <span className="error">{errors.previousEmployer}</span>}
            </div>
            <div>
              <label>Previous Designation:</label><br />
              <input type="text" name="previousDesignation" value={formData.previousDesignation} onChange={handleChange} />
              {errors.previousDesignation && <span className="error">{errors.previousDesignation}</span>}
            </div>
            <div>
              <label>Years of Experience:</label><br />
              <input type="number" name="yearsOfExperience" value={formData.yearsOfExperience} onChange={handleChange} />
              {errors.yearsOfExperience && <span className="error">{errors.yearsOfExperience}</span>}
            </div>
          </>
        )}
      </div>

      {/* Joining and Employment Details */}

      <h3>JOINING EMPLOYEE DETAILS</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label>Designation:</label><br />
          <input type="text" name="currentDesignation" value={formData.currentDesignation} onChange={handleChange} />
          {errors.currentDesignation && <span className="error">{errors.currentDesignation}</span>}
        </div>
        <div>
          <label>Department:</label><br />
          <input type="text" name="department" value={formData.department} onChange={handleChange} />
          {errors.department && <span className="error">{errors.department}</span>}
        </div>
        <div>
          <label>Employee ID:</label><br />
          <input type="text" name="employeeId" value={formData.employeeId} onChange={handleChange} />
          {errors.employeeId && <span className="error">{errors.employeeId}</span>}
        </div>
        <div>
          <label>Date of Joining:</label><br />
          <input type="date" name="dateOfJoining" value={formData.dateOfJoining} onChange={handleChange} />
          {errors.dateOfJoining && <span className="error">{errors.dateOfJoining}</span>}
        </div>
      </div>

       

      {/* Documents Submitted */}
      <h3>DOCUMENT TO BE SUBMITTED</h3>
      <div>
        <label><input type="checkbox" name="aadhaar" checked={formData.aadhaar} onChange={handleChange} /> Aadhaar/Passport/ID Proof</label><br />
        <label><input type="checkbox" name="panCard" checked={formData.panCard} onChange={handleChange} /> PAN Card</label><br />
        <label><input type="checkbox" name="educationCertificates" checked={formData.educationCertificates} onChange={handleChange} /> Educational Certificates (Degree Certificate with pass status)</label><br />
        <label><input type="checkbox" name="experienceCertificates" checked={formData.experienceCertificates} onChange={handleChange} /> Experience Certificates</label><br />
        <label><input type="checkbox" name="passportPhoto" checked={formData.passportPhoto} onChange={handleChange} /> Personal photo (passport size)</label><br />
      </div>

      {/* Declaration */}
      <h3>DECLARATION</h3>
      <div>
        <label>I, {formData.firstName} {formData.lastName}, confirm that the above-provided details are true and accurate. I agree to abide by the company policies and procedures.</label><br />
        <div style={{ display: 'flex', gap: '20px', alignItems: 'center' }}>

        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <label htmlFor="declarationDate">Date:</label>
          <input type="date" id="declarationDate" name="declarationDate" value={formData.declarationDate} onChange={handleChange}/>
        </div>


      {/* <div style={{ flex: 1 }}>
         <label>Date:</label><br />
        <input type="date" name="declarationDate" value={formData.declarationDate} onChange={handleChange}/>
     </div> */}
     </div>
      </div>
      
        
      <label><input type="checkbox" name="agreedTerms" checked={formData.agreedTerms} onChange={handleChange} /> "I Agree" </label>
      {errors.agreedTerms && <span className="error">{errors.agreedTerms}</span>}
      
        <br />
         <button type="submit">Submit</button>
        <button type="button" onClick={handleReset} style={{ marginLeft: '10px' }}>Reset</button>
        </form>
      </div>
    </div>
  );
}

export default EmployeeRegistrationForm;
